// src/pages/Home.jsx
import PageWrapper from "../components/PageWrapper";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  User2,
  IdCard,
  Rss,
  Linkedin,
  Github,
  Gauge,
  Trophy,
  MessageSquareText,
  LogOut,
  Grid2X2,
} from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

/* ===== Scroll-in animation (per card) ===== */
const appear = (i = 0) => ({
  hidden: { opacity: 0, y: 24, scale: 0.98 },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      delay: i * 0.03,
      type: "spring",
      stiffness: 220,
      damping: 18,
      mass: 1.1,
    },
  },
});

/* Floating link-out icon (door-with-arrow) */
const IconGo = ({ to, href, label = "Open", size = 16 }) => {
  const base =
    "inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 p-2.5 hover:bg-white/20 transition z-20";
  const icon = <LogOut size={size} />;
  return href ? (
    <a
      className={base}
      href={href}
      target="_blank"
      rel="noreferrer"
      aria-label={label}
    >
      {icon}
    </a>
  ) : (
    <Link className={base} to={to} aria-label={label}>
      {icon}
    </Link>
  );
};

/* Spacing helpers so IconGo never covers text */
const rightSafeGap = "pr-12"; // ~48px on the right
const SAFE_BR = "pr-[60px] pb-[60px]"; // reserve ~60px bottom-right

/* Heights (desktop) + profile width */
const CARD_H = "md:h-[220px]";
const PROFILE_H = "md:h-[270px]";
const PROFILE_COLS = "md:col-span-7";

/* Common viewport settings so animations trigger as you scroll */
const inView = {
  initial: "hidden",
  whileInView: "visible",
  viewport: { once: true, amount: 0.3, margin: "0px 0px -10px 0px" },
};

export default function Home() {
  const { language } = useLanguage();
  const t = translations[language] ?? {};

  const TXT = {
    name: "Mohammad Rajabi",
    role: "Web Developer",
    tag: "APIs, React, Tailwind, Framer",
    moreAbout: "Credentials",
    projects: t.projects || "Projects",
    blog: "My Blog",
    services: "Services Offering",
    profiles: "Profiles",
    yearsExp: "YEARS\nEXPERIENCE",
    totalProjects: "TOTAL\nPROJECTS",
    lets: "Let’s",
    workTogether: "Work together",
  };

  return (
    <PageWrapper>
      {/* tiny marquee CSS, scoped to this page */}
      <style>{`
        @keyframes marqueeLTR {
          0% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }
        .marquee-track {
          animation: marqueeLTR var(--marquee-duration, 10s) linear infinite; /* faster (was 18s)
          animation-delay: var(--marquee-delay, -5s); /* start mid-run so it's visible immediately
          white-space: nowrap;
          will-change: transform;
        }
      `}</style>

      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-24 md:pb-28">
        <div className="mx-auto max-w-6xl px-4 py-8 md:py-12">
          {/* GRID */}
          <div className="grid grid-cols-12 gap-3 md:gap-3">
            {/* Profile */}
            <motion.div
              {...inView}
              variants={appear(0)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-12 ${PROFILE_COLS} rounded-3xl bg-gradient-to-b from-[#1b1c1f] to-[#131416] shadow-lg p-4 flex items-center gap-6 ${PROFILE_H}`}
            >
              <img
                src="/certs/mmdusa.jpg"
                alt="profile"
                className="h-28 w-28 rounded-xl object-cover"
              />
              <div className="flex-1">
                <p className="text-xs text-white/50">{TXT.role}</p>
                <h2 className="mt-1 text-2xl font-bold">{TXT.name}</h2>
                <p className="mt-1 text-sm text-white/60">{TXT.tag}</p>
              </div>
              <User2 className="opacity-40 shrink-0" />
            </motion.div>

            {/* Right column (Ticker + 2 squares) */}
            <div className="col-span-12 md:col-span-5 grid grid-cols-12 gap-1 md:gap-1">
              {/* Ticker */}
              <motion.div
                {...inView}
                variants={appear(0.5)}
                className="col-span-12 rounded-2xl bg-[#17181b] shadow-lg h-[42px] md:h-[46px] overflow-hidden px-4 flex items-center"
              >
                <div className="marquee-track text-[12px] md:text-[13px] text-white/70">
                  HTML • CSS • JavaScript —{" "}
                  <span className="text-white">Frameworks/Libraries:</span>{" "}
                  React, Tailwind, Framer Motion —{" "}
                  <span className="text-white">Other:</span> REST APIs, Vercel,
                  Netlify, GitHub
                </div>
              </motion.div>

              {/* Credentials */}
              <motion.div
                {...inView}
                variants={appear(0.7)}
                whileHover={{ scale: 1.01 }}
                className={`relative col-span-12 md:col-span-6 order-1 md:order-none rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} ${SAFE_BR} px-4 md:px-5 pt-10`}
              >
                <div className="absolute bottom-4 left-4">
                  <p className="text-[11px] text-white/50 uppercase tracking-wide">
                    More about me
                  </p>
                  <h3 className="text-2xl font-bold text-white leading-none">
                    {TXT.moreAbout}
                  </h3>
                </div>
                <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                  <IdCard />
                </div>
                <div className="absolute right-3 bottom-3 md:right-4 md:bottom-4">
                  <IconGo
                    to="/credentials"
                    label="Open credentials"
                    size={18}
                  />
                </div>
              </motion.div>

              {/* Projects */}
              <motion.div
                {...inView}
                variants={appear(0.9)}
                whileHover={{ scale: 1.01 }}
                className={`relative col-span-12 md:col-span-6 order-2 md:order-none rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} ${SAFE_BR} px-4 md:px-5 pt-10`}
              >
                <div className="absolute bottom-4 left-4">
                  <p className="text-[11px] text-white/50 uppercase tracking-wide">
                    Showcase
                  </p>
                  <h3 className="text-2xl font-bold text-white leading-none">
                    {TXT.projects}
                  </h3>
                </div>
                <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                  <Grid2X2 />
                </div>
                <div className="absolute right-3 bottom-3 md:right-4 md:bottom-4">
                  <IconGo to="/projects" label="Open projects" size={18} />
                </div>
              </motion.div>
            </div>

            {/* Blog */}
            <motion.div
              {...inView}
              variants={appear(1.1)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-4 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} ${SAFE_BR} px-4 md:px-5 pt-10`}
            >
              <div className="absolute bottom-4 left-4">
                <p className="text-1xl font-bold text-white/50">Blog</p>
                <h3 className="text-2xl font-bold text-white leading-none">
                  {TXT.blog}
                </h3>
              </div>
              <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                <Rss />
              </div>
              <div className="absolute right-3 bottom-3 md:right-4 md:bottom-4">
                <IconGo to="/blog" label="Open blog" size={18} />
              </div>
            </motion.div>

            {/* Services */}
            <motion.div
              {...inView}
              variants={appear(1.3)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} px-4 md:px-5 pt-4 pb-12 md:pt-10 md:pb-6`}
            >
              <div className="flex justify-center gap-6 opacity-90 mb-6 md:mb-8">
                <img
                  src="https://skillicons.dev/icons?i=nextjs"
                  alt="Next.js"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/react/ffffff"
                  alt="React"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/tailwindcss/ffffff"
                  alt="Tailwind CSS"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://skillicons.dev/icons?i=js"
                  alt="JavaScript"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/github/ffffff"
                  alt="GitHub"
                  className="h-10 w-10"
                  loading="lazy"
                />
              </div>

              <div className="mt-2 md:mt-0 md:absolute md:bottom-4 md:left-4">
                <p className="text-1xl font-bold text-white/50">
                  Specialization
                </p>
                <h3 className="text-2xl font-bold">{TXT.services}</h3>
              </div>
            </motion.div>

            {/* Profiles */}
            <motion.div
              {...inView}
              variants={appear(1.5)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-2 rounded-2xl bg-[#17181b] shadow-lg
                          h-[160px] md:h-[220px] ${rightSafeGap} ${SAFE_BR}
                          px-4 md:px-5 overflow-hidden`}
            >
              <div className="absolute left-4 top-3 flex items-center gap-2 md:gap-3">
                <a
                  href="https://linkedin.com/in/mohammadhosseinrajabi"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-2.5 md:p-3 hover:bg-white/20 transition"
                  aria-label="LinkedIn"
                >
                  <Linkedin size={18} />
                </a>
                <a
                  href="https://github.com/yourusername"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-2.5 md:p-3 hover:bg-white/20 transition"
                  aria-label="GitHub"
                >
                  <Github size={18} />
                </a>
              </div>

              <div className="absolute left-4 bottom-4">
                <p className="text-[12px] md:text-[13px] font-semibold text-white/70 leading-none">
                  Stay with me
                </p>
                <h3 className="text-xl md:text-2xl font-bold text-white mt-2 md:mt-3 leading-none">
                  {TXT.profiles}
                </h3>
              </div>

              <div className="absolute right-3 bottom-3 md:right-4 md:bottom-4">
                <IconGo to="/profiles" label="Open profiles" size={18} />
              </div>
            </motion.div>

            {/* Stats (side-by-side on phones) */}
            <motion.div
              {...inView}
              variants={appear(1.7)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-6 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2 ${CARD_H}`}
            >
              <div className="rounded-2xl bg-black/20 p-6">
                <Gauge />
              </div>
              <div className="text-3xl font-bold">01</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                YEARS{"\n"}EXPERIENCE
              </p>
            </motion.div>

            <motion.div
              {...inView}
              variants={appear(1.9)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-6 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2 ${CARD_H}`}
            >
              <div className="rounded-2xl bg-black/20 p-6">
                <Trophy />
              </div>
              <div className="text-3xl font-bold">06</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                TOTAL{"\n"}PROJECTS
              </p>
            </motion.div>

            {/* CTA */}
            <motion.div
              {...inView}
              variants={appear(2.1)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-gradient-to-br from-[#17181b] to-[#101114] shadow-lg ${CARD_H} ${rightSafeGap} ${SAFE_BR} p-6 md:p-8 overflow-hidden`}
            >
              <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                <MessageSquareText size={36} />
              </div>

              <div className="flex h-full items-start">
                <div className="mt-12">
                  <p className="text-3xl font-bold text-white leading-none">
                    Let’s
                  </p>
                  <p className="text-4xl font-bold text-white mt-3 leading-none">
                    Work <span className="text-purple-400">together</span>
                  </p>
                </div>
              </div>

              <div className="absolute right-3 bottom-3 md:right-4 md:bottom-4">
                <IconGo to="/contact" label="Contact" size={18} />
              </div>
            </motion.div>
          </div>
        </div>
      </section>
    </PageWrapper>
  );
}
