// src/components/Footer.jsx
import { Code2 } from "lucide-react";
import { Link } from "react-router-dom";

export default function Footer() {
  const year = new Date().getFullYear();

  return (
    <footer className="bg-[#0f0f11] border-t border-white/10 text-white">
      <div className="max-w-6xl mx-auto px-4 py-4 text-center space-y-6">
        {/* Logo + Name */}
        <div className="flex items-center justify-center gap-2">
          <img
            src="/logo.png" // same PNG in public folder
            alt="Logo"
            className="w-6 h-6 object-contain"
          />
          <span className="text-lg font-semibold">Mohammad Rajabi</span>
        </div>

        {/* Footer nav */}
        <nav className="flex flex-wrap justify-center gap-x-8 gap-y-3 text-sm text-white/60">
          <Link to="/" className="hover:text-white transition">
            Home
          </Link>
          <Link to="/about" className="hover:text-white transition">
            About
          </Link>
          <Link to="/projects" className="hover:text-white transition">
            Projects
          </Link>
          <Link to="/contact" className="hover:text-white transition">
            Contact
          </Link>
        </nav>

        {/* Credit line */}
        <p className="text-sm text-white/60 flex items-center justify-center gap-2">
          <Code2 size={14} className="opacity-80" />
          Developed by <span className="text-white">Mohammad Rajabi</span>. Â©{" "}
          {year}
        </p>
      </div>
    </footer>
  );
}
