import { NavLink, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

const cx = (...c) => c.filter(Boolean).join(" ");

export default function Navbar() {
  const [open, setOpen] = useState(false);
  const { language, setLanguage } = useLanguage();
  const t = translations[language];

  useEffect(() => {
    document.documentElement.classList.add("dark"); // Always dark mode
  }, []);

  const linkBase = "px-3 py-1.5 text-[14px] rounded-lg transition";
  const linkIdle = "text-white/70 hover:text-white";
  const linkActive = "text-white bg-white/5";

  return (
    <header className="sticky top-0 z-50 bg-transparent">
      <div className="mx-auto max-w-6xl px-4">
        <div className="h-14 flex items-center justify-between gap-3">
          {/* Logo / Name */}
          <Link to="/" className="flex items-center gap-2">
            <span className="h-5 w-5 rounded-full bg-white/10" />
            <span className="text-white font-semibold tracking-wide">
              Mohammad Rajabi
            </span>
          </Link>

          {/* Navigation */}
          <nav className="hidden md:flex items-center gap-2">
            <NavLink
              to="/"
              end
              className={({ isActive }) =>
                cx(linkBase, isActive ? linkActive : linkIdle)
              }
            >
              {t.home}
            </NavLink>
            <NavLink
              to="/about"
              className={({ isActive }) =>
                cx(linkBase, isActive ? linkActive : linkIdle)
              }
            >
              {t.about}
            </NavLink>
            <NavLink
              to="/projects"
              className={({ isActive }) =>
                cx(linkBase, isActive ? linkActive : linkIdle)
              }
            >
              {t.projects}
            </NavLink>
            <NavLink
              to="/contact"
              className={({ isActive }) =>
                cx(linkBase, isActive ? linkActive : linkIdle)
              }
            >
              {t.contact}
            </NavLink>
          </nav>

          {/* Right-side actions */}
          <div className="flex items-center gap-2">
            {/* Language Selector */}
            <div className="relative lang-toggle">
              <button
                onClick={() => setOpen((v) => !v)}
                className="h-8 pl-2 pr-2.5 rounded-full bg-white/5 text-white/80 hover:text-white flex items-center gap-1.5"
              >
                <img
                  src={`/flags/${language}.png`}
                  alt={language}
                  className="w-4 h-4 rounded object-cover"
                />
                <span className="uppercase text-[12px]">{language}</span>
              </button>
              {open && (
                <div className="absolute right-0 mt-2 w-28 rounded-lg bg-[#121317] border border-white/10 overflow-hidden">
                  <button
                    onClick={() => {
                      setLanguage("en");
                      setOpen(false);
                    }}
                    className="w-full px-3 py-2 text-left text-white/80 hover:bg-white/5 flex items-center gap-2 text-sm"
                  >
                    <img
                      src="/flags/en.png"
                      alt="EN"
                      className="w-4 h-4 rounded"
                    />
                    EN
                  </button>
                  <button
                    onClick={() => {
                      setLanguage("it");
                      setOpen(false);
                    }}
                    className="w-full px-3 py-2 text-left text-white/80 hover:bg-white/5 flex items-center gap-2 text-sm"
                  >
                    <img
                      src="/flags/it.png"
                      alt="IT"
                      className="w-4 h-4 rounded"
                    />
                    IT
                  </button>
                </div>
              )}
            </div>

            {/* Let's Talk Button */}
            <Link
              to="/contact"
              className="h-8 px-3 rounded-full bg-indigo-600 text-white text-[13px] font-semibold hover:bg-indigo-500 pt-[5px]"
            >
              Letâ€™s Talk
            </Link>
          </div>
        </div>
      </div>
    </header>
  );
}
