// src/pages/Home.jsx
import PageWrapper from "../components/PageWrapper";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Grid3X3,
  User2,
  IdCard,
  Rss,
  Linkedin,
  Github,
  Gauge,
  Trophy,
  MessageSquareText,
  LogOut,
  Grid2X2,
} from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

/* Animations */
const gridStagger = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.35, staggerChildren: 0.16 },
  },
};
const popIn = (i = 0) => {
  const d = i % 3;
  const offset = d === 0 ? { x: -60 } : d === 1 ? { x: 60 } : { y: 60 };
  return {
    hidden: { opacity: 0, scale: 0.96, ...offset },
    visible: {
      opacity: 1,
      scale: 1,
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 120, damping: 22, mass: 1.2 },
    },
  };
};

/* Link-out icon */
const IconGo = ({ to, href, label = "Open", size = 16 }) => {
  const base =
    "inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 p-2.5 hover:bg-white/20 transition z-20";
  const icon = <LogOut size={size} />;
  return href ? (
    <a
      className={`${base}`}
      href={href}
      target="_blank"
      rel="noreferrer"
      aria-label={label}
    >
      {icon}
    </a>
  ) : (
    <Link className={`${base}`} to={to} aria-label={label}>
      {icon}
    </Link>
  );
};

/* Layout helpers (desktop sizes unchanged; we only add mobile min heights/padding) */
const CARD_H = "h-[180px] md:h-[220px]"; // squares & wide cards
const PROFILE_H = "h-[220px] md:h-[270px]"; // profile hero
const rightSafeGap = "pr-12"; // avoids overlap with IconGo

export default function Home() {
  const { language } = useLanguage();
  const t = translations[language] ?? {};

  const TXT = {
    name: "Mohammad Rajabi",
    role: "Web Developer",
    tag: "APIs, React, Tailwind, Framer",
    moreAbout: "Credentials",
    projects: t.projects || "Projects",
    blog: "My Blog",
    services: "Services Offering",
    profiles: "Profiles",
    lets: "Let’s",
    workTogether: "Work together",
  };

  return (
    <PageWrapper>
      <style>{`
        @keyframes marqueeLTR { 0% { transform: translateX(100%);} 100% { transform: translateX(-100%);} }
        .marquee-track { animation: marqueeLTR 18s linear infinite; white-space: nowrap; will-change: transform; }
      `}</style>

      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-24 md:pb-28">
        <div className="mx-auto max-w-6xl px-4 py-8 md:py-12">
          <motion.div
            className="grid grid-cols-12 gap-3 md:gap-3"
            variants={gridStagger}
            initial="hidden"
            animate="visible"
          >
            {/* Profile */}
            <motion.div
              variants={popIn(0)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-7 rounded-3xl bg-gradient-to-b from-[#1b1c1f] to-[#131416] shadow-lg p-4 md:p-5 flex items-center gap-6 ${PROFILE_H}`}
            >
              <img
                src="/certs/mmdusa.jpg"
                alt="profile"
                className="h-28 w-28 rounded-xl object-cover"
              />
              <div className="flex-1">
                <p className="text-xs text-white/50">{TXT.role}</p>
                <h2 className="mt-1 text-2xl font-bold">{TXT.name}</h2>
                <p className="mt-1 text-sm text-white/60">{TXT.tag}</p>
              </div>
              <User2 className="opacity-40 shrink-0" />
            </motion.div>

            {/* Right column (ticker + squares) */}
            <div className="col-span-12 md:col-span-5 grid grid-cols-12 gap-2 md:gap-2">
              {/* Ticker — hide on mobile to avoid collisions */}
              <motion.div
                variants={popIn(1)}
                className="col-span-12 hidden md:flex rounded-2xl bg-[#17181b] shadow-lg h-[46px] overflow-hidden px-4 items-center"
              >
                <div className="marquee-track text-[13px] text-white/70">
                  HTML • CSS • JavaScript —{" "}
                  <span className="text-white">Frameworks/Libraries:</span>{" "}
                  React, Tailwind, Framer Motion —{" "}
                  <span className="text-white">Other:</span> REST APIs, Vercel,
                  Netlify, GitHub
                </div>
              </motion.div>

              {/* Credentials */}
              <motion.div
                variants={popIn(2)}
                whileHover={{ scale: 1.01 }}
                className={`relative col-span-12 md:col-span-6 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} px-4 md:px-5 pt-10 pb-14`}
              >
                {/* top-left big icon */}
                <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                  <IdCard />
                </div>
                {/* text bottom-left */}
                <div className="absolute left-4 bottom-4">
                  <p className="text-[11px] text-white/50 uppercase tracking-wide">
                    More about me
                  </p>
                  <h3 className="text-2xl md:text-2xl font-bold">
                    {TXT.moreAbout}
                  </h3>
                </div>
                {/* IconGo bottom-right (mobile+desktop) */}
                <div className="absolute right-3 bottom-3">
                  <IconGo
                    to="/credentials"
                    label="Open credentials"
                    size={18}
                  />
                </div>
              </motion.div>

              {/* Projects */}
              <motion.div
                variants={popIn(3)}
                whileHover={{ scale: 1.01 }}
                className={`relative col-span-12 md:col-span-6 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} px-4 md:px-5 pt-10 pb-14`}
              >
                <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                  <Grid2X2 />
                </div>
                <div className="absolute left-4 bottom-4">
                  <p className="text-[11px] text-white/50 uppercase tracking-wide">
                    Showcase
                  </p>
                  <h3 className="text-2xl md:text-2xl font-bold">
                    {TXT.projects}
                  </h3>
                </div>
                <div className="absolute right-3 bottom-3">
                  <IconGo to="/projects" label="Open projects" size={18} />
                </div>
              </motion.div>
            </div>

            {/* Blog */}
            <motion.div
              variants={popIn(4)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-4 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} px-4 md:px-5 pt-10 pb-14`}
            >
              <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                <Rss />
              </div>
              <div className="absolute left-4 bottom-4">
                <p className="text-sm text-white/50">Blog</p>
                <h3 className="text-2xl font-bold">{TXT.blog}</h3>
              </div>
              <div className="absolute right-3 bottom-3">
                <IconGo to="/blog" label="Open blog" size={18} />
              </div>
            </motion.div>

            {/* Services */}
            <motion.div
              variants={popIn(5)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} px-4 md:px-5 pt-8 pb-14`}
            >
              <div className="flex justify-center gap-6 opacity-90">
                <img
                  src="https://skillicons.dev/icons?i=nextjs"
                  alt="Next.js"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/react/ffffff"
                  alt="React"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/tailwindcss/ffffff"
                  alt="Tailwind CSS"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://skillicons.dev/icons?i=js"
                  alt="JavaScript"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/github/ffffff"
                  alt="GitHub"
                  className="h-10 w-10"
                  loading="lazy"
                />
              </div>
              <div className="absolute left-4 bottom-4">
                <p className="text-sm text-white/50">Specialization</p>
                <h3 className="text-2xl font-bold"> {TXT.services}</h3>
              </div>
            </motion.div>

            {/* Profiles */}
            <motion.div
              variants={popIn(6)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-2 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} ${rightSafeGap} px-4 md:px-5 pt-12 pb-16`}
            >
              {/* text */}
              <div className="absolute left-4 bottom-4">
                <p className="text-sm text-white/60">Stay with me</p>
                <h3 className="text-2xl font-bold">{TXT.profiles}</h3>
              </div>
              {/* socials top-left on mobile so they don’t collide */}
              <div className="absolute left-3 top-3 flex items-center gap-2">
                <a
                  href="https://linkedin.com/in/mohammadhosseinrajabi"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-3 hover:bg-white/20 transition"
                  aria-label="LinkedIn"
                >
                  <Linkedin size={18} />
                </a>
                <a
                  href="https://github.com/yourusername"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-3 hover:bg-white/20 transition"
                  aria-label="GitHub"
                >
                  <Github size={18} />
                </a>
              </div>
              {/* link-out bottom-right */}
              <div className="absolute right-3 bottom-3">
                <IconGo to="/profiles" label="Open profiles" size={18} />
              </div>
            </motion.div>

            {/* Stat 1 */}
            <motion.div
              variants={popIn(7)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} flex flex-col items-center justify-center gap-2 p-6`}
            >
              <div className="rounded-2xl bg-black/20 p-6">
                <Gauge />
              </div>
              <div className="text-3xl font-bold">01</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                YEARS{"\n"}EXPERIENCE
              </p>
            </motion.div>

            {/* Stat 2 */}
            <motion.div
              variants={popIn(8)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] shadow-lg ${CARD_H} flex flex-col items-center justify-center gap-2 p-6`}
            >
              <div className="rounded-2xl bg-black/20 p-6">
                <Trophy />
              </div>
              <div className="text-3xl font-bold">06</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                TOTAL{"\n"}PROJECTS
              </p>
            </motion.div>

            {/* CTA */}
            <motion.div
              variants={popIn(9)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-gradient-to-br from-[#17181b] to-[#101114] shadow-lg ${CARD_H} ${rightSafeGap} p-6 md:p-8`}
            >
              <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                <MessageSquareText size={36} />
              </div>
              <div className="flex h-full items-start">
                <div className="mt-10 md:mt-12">
                  <p className="text-3xl font-bold text-white leading-none">
                    Let’s
                  </p>
                  <p className="text-4xl font-bold text-white mt-3 leading-none">
                    Work <span className="text-purple-400">together</span>
                  </p>
                </div>
              </div>
              <div className="absolute right-3 bottom-3">
                <IconGo to="/contact" label="Contact" size={18} />
              </div>
            </motion.div>
          </motion.div>
        </div>
      </section>
    </PageWrapper>
  );
}
