import PageWrapper from "../components/PageWrapper";
import { posts } from "../data/posts";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

const grid = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.2, staggerChildren: 0.12 },
  },
};
const card = {
  hidden: { opacity: 0, y: 24 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { type: "spring", stiffness: 120, damping: 18 },
  },
};

export default function Blog() {
  return (
    <PageWrapper>
      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-24 md:pb-28">
        <div className="mx-auto max-w-6xl px-4 pt-10 md:pt-12">
          <h1 className="text-center text-2xl md:text-3xl font-extrabold tracking-wide mb-8">
            Blog
          </h1>

          <motion.div
            variants={grid}
            initial="hidden"
            animate="visible"
            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6"
          >
            {posts.map((p) => (
              <motion.article
                key={p.slug}
                variants={card}
                className="rounded-2xl bg-[#17181b] shadow-lg overflow-hidden flex flex-col"
              >
                <div className="h-48 md:h-52 bg-black/20 grid place-content-center">
                  <img
                    src={p.cover}
                    alt={p.title}
                    className="h-24 w-24 md:h-28 md:w-28 object-contain opacity-90"
                    loading="lazy"
                  />
                </div>

                <div className="p-5 md:p-6 flex-1 flex flex-col">
                  <div className="flex items-center gap-2 text-xs text-white/60">
                    <span className="inline-block h-6 w-6 rounded-full bg-white/10" />
                    <span className="capitalize">{p.author.toLowerCase()}</span>
                  </div>

                  <h3 className="mt-3 text-lg font-semibold">{p.title}</h3>
                  <p className="mt-2 text-[13px] text-white/70 flex-1">
                    {p.excerpt}
                  </p>

                  <div className="mt-4">
                    <Link
                      to={`/blog/${p.slug}`}
                      className="inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 px-4 py-2 text-sm font-medium hover:bg-white/20 transition"
                    >
                      READ MORE
                    </Link>
                  </div>
                </div>
              </motion.article>
            ))}
          </motion.div>
        </div>
      </section>
    </PageWrapper>
  );
}
