// src/pages/Home.jsx
import PageWrapper from "../components/PageWrapper";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Grid3X3,
  User2,
  IdCard,
  Rss,
  Linkedin,
  Github,
  Gauge,
  Trophy,
  MessageSquareText,
  LogOut,
} from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

/* Animations (gentle + visible) */
const gridStagger = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.35, staggerChildren: 0.16 },
  },
};
const popIn = (i = 0) => {
  const d = i % 3;
  const offset = d === 0 ? { x: -60 } : d === 1 ? { x: 60 } : { y: 60 };
  return {
    hidden: { opacity: 0, scale: 0.96, ...offset },
    visible: {
      opacity: 1,
      scale: 1,
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 120, damping: 22, mass: 1.2 },
    },
  };
};

/* Floating link-out icon (door-with-arrow) */
const IconGo = ({ to, href, label = "Open", size = 16 }) => {
  const base =
    "absolute top-3 right-3 inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 p-2.5 hover:bg-white/20 transition z-20";
  const icon = <LogOut size={size} />;
  return href ? (
    <a
      className={base}
      href={href}
      target="_blank"
      rel="noreferrer"
      aria-label={label}
    >
      {icon}
    </a>
  ) : (
    <Link className={base} to={to} aria-label={label}>
      {icon}
    </Link>
  );
};

// Reserve space so IconGo never overlaps content/icons
const rightSafeGap = "pr-12"; // ~48px reserved area

// Equal heights so profile aligns perfectly with the two squares
const CARD_H = "md:h-[220px]";
const PROFILE_H = "md:h-[270px]";
const PROFILE_COLS = "md:col-span-7";
export default function Home() {
  const { language } = useLanguage();
  const t = translations[language] ?? {};

  const TXT = {
    name: "Mohammad Rajabi",
    role: "Web Developer",
    tag: "APIs, React, Tailwind, Framer",
    moreAbout: "Credentials",
    projects: t.projects || "Projects",
    blog: "My Blog",
    services: "Services Offering",
    profiles: "Profiles",
    yearsExp: "YEARS\nEXPERIENCE",
    totalProjects: "TOTAL\nPROJECTS",
    lets: "Let’s",
    workTogether: "Work together",
  };

  return (
    <PageWrapper>
      {/* tiny marquee CSS, scoped to this page */}
      <style>{`
        @keyframes marqueeLTR {
          0% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }
        .marquee-track {
          animation: marqueeLTR 18s linear infinite;
          white-space: nowrap;
          will-change: transform;
        }
      `}</style>

      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-24 md:pb-28">
        <div className="mx-auto max-w-6xl px-4 py-8 md:py-12">
          {/* GRID — reduced gaps everywhere */}
          <motion.div
            className="grid grid-cols-12 gap-3 md:gap-1.5"
            variants={gridStagger}
            initial="hidden"
            animate="visible"
          >
            {/* Profile card */}
            <motion.div
              variants={popIn(0)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-12 ${PROFILE_COLS} rounded-3xl bg-gradient-to-b from-[#1b1c1f] to-[#131416] shadow-lg p-4 flex items-center gap-6 ${PROFILE_H}`}
            >
              <img
                src="/certs/mmdusa.jpg"
                alt="profile"
                className="h-28 w-28 rounded-xl object-cover"
              />
              <div className="flex-1">
                <p className="text-xs text-white/50">{TXT.role}</p>
                <h2 className="mt-1 text-2xl font-bold">{TXT.name}</h2>
                <p className="mt-1 text-sm text-white/60">{TXT.tag}</p>
              </div>
              <User2 className="opacity-40 shrink-0" />
            </motion.div>

            {/* Right column (5 cols): Ticker + 2 squares side-by-side */}
            <div className="col-span-12 md:col-span-5 grid grid-cols-12 gap-1 md:gap-1">
              {/* Ticker (full width) */}
              <motion.div
                variants={popIn(1)}
                className="col-span-12 rounded-2xl bg-[#17181b] shadow-lg h-[42px] md:h-[46px] overflow-hidden px-4 flex items-center"
              >
                <div className="marquee-track text-[12px] md:text-[13px] text-white/70">
                  HTML • CSS • JavaScript —{" "}
                  <span className="text-white">Frameworks/Libraries:</span>{" "}
                  React, Tailwind, Framer Motion —{" "}
                  <span className="text-white">Other:</span> REST APIs, Vercel,
                  Netlify, GitHub
                </div>
              </motion.div>

              {/* Credentials (square) */}
              <motion.div
                variants={popIn(2)}
                whileHover={{ scale: 1.01 }}
                className={`relative col-span-6 rounded-2xl bg-[#17181b] p-4 shadow-lg flex items-center justify-between ${rightSafeGap} ${CARD_H}`}
              >
                <IconGo to="/credentials" label="Open credentials" />
                <div>
                  <p className="text-[11px] text-white/50 uppercase tracking-wide">
                    More about me
                  </p>
                  <h3 className="mt-1 text-lg font-semibold">
                    {TXT.moreAbout}
                  </h3>
                </div>
                <div className="shrink-0 mr-1 pointer-events-none">
                  <IdCard />
                </div>
              </motion.div>

              {/* Projects (square) */}
              <motion.div
                variants={popIn(3)}
                whileHover={{ scale: 1.01 }}
                className={`relative col-span-6 rounded-2xl bg-[#17181b] p-4 shadow-lg flex items-center justify-between ${rightSafeGap} ${CARD_H}`}
              >
                <IconGo to="/projects" label="Open projects" />
                <div>
                  <p className="text-[11px] text-white/50 uppercase tracking-wide">
                    Showcase
                  </p>
                  <h3 className="mt-1 text-lg font-semibold">{TXT.projects}</h3>
                </div>
                <div className="shrink-0 mr-1 pointer-events-none">
                  <Grid3X3 />
                </div>
              </motion.div>
            </div>

            {/* ROW 2: Blog (square) + Services (wide) + Profiles (square) */}
            <motion.div
              variants={popIn(4)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-4 rounded-2xl bg-[#17181b] p-4 shadow-lg flex items-center justify-between ${rightSafeGap} ${CARD_H}`}
            >
              <IconGo to="/blog" label="Open blog" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Blog</p>
                <h3 className="text-lg font-semibold">{TXT.blog}</h3>
              </div>
              <div className="shrink-0 mr-1 pointer-events-none">
                <Rss />
              </div>
            </motion.div>

            <motion.div
              variants={popIn(5)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-[#17181b] p-4 shadow-lg flex flex-col justify-center ${CARD_H}`}
            >
              {/* Logos row centered */}
              <div className="flex justify-center gap-6 opacity-90 mb-4">
                <img
                  src="https://skillicons.dev/icons?i=nextjs"
                  alt="Next.js"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/react/ffffff"
                  alt="React"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/tailwindcss/ffffff"
                  alt="Tailwind CSS"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://skillicons.dev/icons?i=js"
                  alt="JavaScript"
                  className="h-10 w-10"
                  loading="lazy"
                />
                <img
                  src="https://cdn.simpleicons.org/github/ffffff"
                  alt="GitHub"
                  className="h-10 w-10"
                  loading="lazy"
                />
              </div>

              {/* Text left-aligned like before */}
              <div className="space-y-1 text-left mt-4">
                <p className="text-sm text-white/50">Specialization</p>
                <h3 className="text-2xl font-bold">{TXT.services}</h3>
              </div>
            </motion.div>

            <motion.div
              variants={popIn(6)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-2 rounded-2xl bg-[#17181b] p-4 shadow-lg overflow-hidden ${rightSafeGap} ${CARD_H}`}
            >
              {/* Link-out button moved to bottom-right */}
              <div className="absolute bottom-20 right-4">
                <IconGo to="/contact" label="Contact" size={18} />
              </div>

              {/* Text - bigger */}
              <div className="absolute left-4 top-10 opacity-80 pointer-events-none">
                <p className="text-1.5xl font-bold text-white leading-none">
                  Stay with me
                </p>
                <h3 className="text-2xl font-bold text-white mt-3 leading-none">
                  {TXT.profiles}
                </h3>
              </div>

              {/* Social icons - slightly bigger */}
              <div className="flex items-center gap-3 mt-4">
                <a
                  href="https://linkedin.com/in/mohammadhosseinrajabi"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-3 hover:bg-white/20 transition"
                  aria-label="LinkedIn"
                >
                  <Linkedin size={20} />
                </a>
                <a
                  href="https://github.com/yourusername"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-3 hover:bg-white/20 transition"
                  aria-label="GitHub"
                >
                  <Github size={20} />
                </a>
              </div>
            </motion.div>

            {/* ROW 3: Stats (3 + 3) + CTA (6) */}
            <motion.div
              variants={popIn(7)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2 ${CARD_H}`}
            >
              <div className="rounded-2xl bg-black/20 p-4">
                <Gauge />
              </div>
              <div className="text-3xl font-bold">01</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                YEARS{"\n"}EXPERIENCE
              </p>
            </motion.div>

            <motion.div
              variants={popIn(8)}
              whileHover={{ scale: 1.01 }}
              className={`col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2 ${CARD_H}`}
            >
              <div className="rounded-2xl bg-black/20 p-4">
                <Trophy />
              </div>
              <div className="text-3xl font-bold">06</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                TOTAL{"\n"}PROJECTS
              </p>
            </motion.div>

            {/* CTA (wide/long) */}
            <motion.div
              variants={popIn(9)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-gradient-to-br from-[#17181b] to-[#101114] p-6 md:p-8 shadow-lg overflow-hidden ${rightSafeGap} ${CARD_H}`}
            >
              {/* speech icon top-left */}
              <div className="absolute left-4 top-4 opacity-80 pointer-events-none">
                <MessageSquareText size={36} />
              </div>

              {/* text — “Let’s” then “Work together” */}
              <div className="flex h-full items-start">
                <div className="mt-12">
                  <p className="text-3xl font-bold text-white leading-none">
                    Let’s
                  </p>
                  <p className="text-4xl font-bold text-white mt-3 leading-none">
                    Work <span className="text-purple-400">together</span>
                  </p>
                </div>
              </div>

              {/* link-out button moved to bottom-right */}
              <div className="absolute bottom-20 right-4">
                <IconGo to="/contact" label="Contact" size={18} />
              </div>
            </motion.div>
          </motion.div>
        </div>
      </section>
    </PageWrapper>
  );
}
