import { NavLink, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

function cx(...classes) {
  return classes.filter(Boolean).join(" ");
}

export default function Navbar() {
  const [darkMode, setDarkMode] = useState(true); // default dark like the screenshot
  const [showLangMenu, setShowLangMenu] = useState(false);
  const { language, setLanguage } = useLanguage();
  const t = translations[language];

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  // close language menu on outside click
  useEffect(() => {
    const onClick = (e) => {
      if (!e.target.closest(".lang-toggle")) setShowLangMenu(false);
    };
    document.addEventListener("click", onClick);
    return () => document.removeEventListener("click", onClick);
  }, []);

  const linkBase = "px-3 py-2 text-sm md:text-[15px] rounded-lg transition";
  const linkIdle = "text-white/60 hover:text-white";
  const linkActive =
    "text-white bg-white/5 shadow-[inset_0_0_0_1px_rgba(255,255,255,.06)]";

  return (
    <nav className="sticky top-0 z-50 w-full bg-[#0d0e10]/80 backdrop-blur border-b border-white/5">
      <div className="mx-auto max-w-6xl px-4">
        <div className="h-14 md:h-16 grid grid-cols-12 items-center gap-2">
          {/* Left: Logo */}
          <div className="col-span-4 md:col-span-3">
            <Link
              to="/"
              className="inline-flex items-center gap-2 group"
              aria-label="Home"
            >
              <span className="h-6 w-6 rounded-full bg-white/10 border border-white/10" />
              <span className="text-white font-semibold tracking-wide group-hover:opacity-90">
                Mohammad Rajabi
              </span>
            </Link>
          </div>

          {/* Center: Nav links */}
          <ul className="col-span-4 md:col-span-6 hidden md:flex items-center justify-center gap-2">
            <li>
              <NavLink
                to="/"
                end
                className={({ isActive }) =>
                  cx(linkBase, isActive ? linkActive : linkIdle)
                }
              >
                {t.home}
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/about"
                className={({ isActive }) =>
                  cx(linkBase, isActive ? linkActive : linkIdle)
                }
              >
                {t.about}
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/projects"
                className={({ isActive }) =>
                  cx(linkBase, isActive ? linkActive : linkIdle)
                }
              >
                {t.projects}
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/contact"
                className={({ isActive }) =>
                  cx(linkBase, isActive ? linkActive : linkIdle)
                }
              >
                {t.contact}
              </NavLink>
            </li>
          </ul>

          {/* Right: Actions */}
          <div className="col-span-8 md:col-span-3 flex items-center justify-end gap-2">
            {/* Download CV (optional â€“ hide on mobile like screenshot) */}
            <a
              href="/mohammad-cv.pdf"
              download
              className="hidden md:inline-flex items-center justify-center h-9 px-3 rounded-lg text-sm text-white/80 hover:text-white bg-white/5 border border-white/10"
            >
              {t.downloadCV}
            </a>

            {/* Dark/Light toggle */}
            <button
              onClick={() => setDarkMode((v) => !v)}
              className="h-9 w-9 grid place-items-center rounded-full bg-white/5 border border-white/10 text-white/80 hover:text-white"
              title="Toggle theme"
            >
              {darkMode ? "ðŸŒ™" : "ðŸŒž"}
            </button>

            {/* Language switcher (compact pill) */}
            <div className="relative lang-toggle">
              <button
                onClick={() => setShowLangMenu((s) => !s)}
                className="h-9 pl-2 pr-3 rounded-full bg-white/5 border border-white/10 flex items-center gap-2 text-white/80 hover:text-white"
                title="Language"
              >
                <img
                  src={`/flags/${language}.png`}
                  alt={language}
                  className="w-4 h-4 rounded object-cover"
                />
                <span className="uppercase text-xs">{language}</span>
              </button>

              {showLangMenu && (
                <div className="absolute right-0 mt-2 w-28 rounded-xl bg-[#121317] border border-white/10 shadow-xl overflow-hidden">
                  <button
                    onClick={() => {
                      setLanguage("en");
                      setShowLangMenu(false);
                    }}
                    className="w-full px-3 py-2 text-left text-white/80 hover:bg-white/5 flex items-center gap-2"
                  >
                    <img
                      src="/flags/en.png"
                      alt="EN"
                      className="w-4 h-4 rounded"
                    />
                    EN
                  </button>
                  <button
                    onClick={() => {
                      setLanguage("it");
                      setShowLangMenu(false);
                    }}
                    className="w-full px-3 py-2 text-left text-white/80 hover:bg-white/5 flex items-center gap-2"
                  >
                    <img
                      src="/flags/it.png"
                      alt="IT"
                      className="w-4 h-4 rounded"
                    />
                    IT
                  </button>
                </div>
              )}
            </div>

            {/* Let's Talk (primary) */}
            <Link
              to="/contact"
              className="ml-1 inline-flex items-center justify-center h-9 px-4 rounded-full bg-indigo-600 text-sm font-semibold text-white hover:bg-indigo-500 transition"
            >
              Letâ€™s Talk
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}
