import { motion } from "framer-motion";
import {
  Mail,
  MessageSquareText,
  Sparkles,
  Linkedin,
  Github,
  Phone,
  Instagram,
  MessageCircle, // used for WhatsApp
  Send, // used for Telegram
} from "lucide-react";
import PageWrapper from "../components/PageWrapper";

/* reuse the same gentle animations you used on Home */
const gridStagger = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.25, staggerChildren: 0.12 },
  },
};
const popIn = (i = 0) => {
  const d = i % 3;
  const offset = d === 0 ? { x: -50 } : d === 1 ? { x: 50 } : { y: 50 };
  return {
    hidden: { opacity: 0, scale: 0.96, ...offset },
    visible: {
      opacity: 1,
      scale: 1,
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 120, damping: 20, mass: 1.1 },
    },
  };
};

export default function Contact() {
  return (
    <PageWrapper>
      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-24 md:pb-28">
        <div className="mx-auto max-w-6xl px-4 py-8 md:py-12">
          {/* Grid layout similar to Home */}
          <motion.div
            className="grid grid-cols-12 gap-3 md:gap-4"
            variants={gridStagger}
            initial="hidden"
            animate="visible"
          >
            {/* LEFT: contact info card (stacks on mobile) */}
            <motion.aside
              variants={popIn(0)}
              className="col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] shadow-lg p-5 md:p-6 h-full"
            >
              <p className="text-xs text-white/50 uppercase tracking-wide">
                Contact Info
              </p>

              {/* Email */}
              <ContactRow
                href="mailto:momo.tkd19@gmail.com"
                label="Mail me"
                text="momo.tkd19@gmail.com"
                Icon={Mail}
              />

              {/* LinkedIn */}
              <ContactRow
                href="https://linkedin.com/in/mohammadhosseinrajabi"
                label="LinkedIn"
                text="mohammadhosseinrajabi"
                Icon={Linkedin}
                newTab
              />

              {/* GitHub */}
              <ContactRow
                href="https://github.com/yourusername"
                label="GitHub"
                text="MmdUsa"
                Icon={Github}
                newTab
              />

              {/* Phone (tap-to-call) */}
              <ContactRow
                href="tel:+1234567890"
                label="Phone"
                text="+39 327 037 6929"
                Icon={Phone}
              />

              {/* Instagram */}
              <ContactRow
                href="https://instagram.com/yourusername"
                label="Instagram"
                text="@yourusername"
                Icon={Instagram}
                newTab
              />

              {/* WhatsApp */}
              <ContactRow
                href="https://wa.me/1234567890"
                label="WhatsApp"
                text="+1 234 567 890"
                Icon={MessageCircle}
                newTab
              />

              {/* Telegram */}
              <ContactRow
                href="https://t.me/yourusername"
                label="Telegram"
                text="@yourusername"
                Icon={Send}
                newTab
              />
            </motion.aside>

            {/* RIGHT: form card (matches Home look) */}
            <motion.section
              variants={popIn(1)}
              className="relative col-span-12 md:col-span-9 rounded-2xl bg-gradient-to-br from-[#17181b] to-[#101114] shadow-lg p-5 md:p-11 pt-16 md:pt-11"
            >
              {/* Decorative icons like your CTA card (behind content on z-0) */}
              <div className="absolute left-3 top-3 md:left-4 md:top-4 opacity-60 pointer-events-none z-0">
                <MessageSquareText size={26} />
              </div>
              <div className="hidden sm:block absolute right-3 top-3 md:right-4 md:top-4 opacity-50 pointer-events-none z-0">
                <Sparkles size={22} />
              </div>

              {/* Title above icons */}
              <div className="relative z-10">
                <h1 className="text-3xl md:text-4xl font-extrabold mb-6">
                  Let’s work <span className="text-purple-400">together</span>
                </h1>

                {/* Form */}
                <form
                  className="space-y-4 md:space-y-5"
                  onSubmit={(e) => {
                    e.preventDefault();
                    // handle submit here (email service / API)
                  }}
                >
                  <FormRow label="Name" placeholder="Your name" />
                  <FormRow
                    label="Email"
                    type="email"
                    placeholder="Your email"
                  />
                  <FormRow
                    label="Your Subject"
                    placeholder="What’s this about?"
                  />

                  <div>
                    <label className="mb-2 block text-sm text-white/70">
                      Your Message
                    </label>
                    <textarea
                      className="w-full rounded-xl bg-black/20 border border-white/10 px-4 py-3 outline-none focus:ring-2 focus:ring-white/20 placeholder-white/40 min-h-[140px]"
                      placeholder="Tell me a little about your project…"
                    />
                  </div>

                  <div className="pt-2">
                    <button
                      type="submit"
                      className="w-full md:w-auto rounded-xl bg-white/10 hover:bg-white/20 border border-white/10 px-6 py-3 font-medium transition"
                    >
                      Send Message
                    </button>
                  </div>
                </form>
              </div>
            </motion.section>
          </motion.div>
        </div>
      </section>
    </PageWrapper>
  );
}

/** Reusable row for the left contact card */
function ContactRow({ href, label, text, Icon, newTab = false }) {
  const target = newTab ? "_blank" : undefined;
  const rel = newTab ? "noreferrer" : undefined;
  return (
    <a
      href={href}
      target={target}
      rel={rel}
      className="mt-3 flex flex-col sm:flex-row items-start sm:items-center gap-2 sm:gap-3 rounded-xl bg-black/20 p-3 hover:bg-black/25 transition"
      aria-label={label}
    >
      <span className="inline-flex h-9 w-9 items-center justify-center rounded-lg bg-white/10 shrink-0">
        <Icon size={18} />
      </span>
      <div className="leading-tight">
        <p className="text-xs text-white/50">{label}</p>
        <span className="text-sm break-all">{text}</span>
      </div>
    </a>
  );
}

/* small input helper to keep the JSX clean */
function FormRow({ label, type = "text", placeholder = "" }) {
  return (
    <div>
      <label className="mb-2 block text-sm text-white/70">{label}</label>
      <input
        type={type}
        placeholder={placeholder}
        className="w-full rounded-xl bg-black/20 border border-white/10 px-4 py-3 outline-none focus:ring-2 focus:ring-white/20 placeholder-white/40"
      />
    </div>
  );
}
