// src/pages/About.jsx
import PageWrapper from "../components/PageWrapper";
import { BookText, Briefcase } from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

/** Small circular progress meter (SVG, responsive) */
function CircularGauge({ label, value, className = "" }) {
  const size = 112; // px
  const stroke = 10;
  const r = (size - stroke) / 2;
  const c = 2 * Math.PI * r;
  const dash = (value / 100) * c;

  return (
    <div
      className={`relative rounded-2xl bg-black/20 shadow-inner grid place-items-center p-4 ${className}`}
      style={{ width: size + 32, height: size + 32 }}
    >
      <svg
        width={size}
        height={size}
        viewBox={`0 0 ${size} ${size}`}
        className="drop-shadow-[0_2px_6px_rgba(0,0,0,0.35)]"
      >
        {/* Track */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={r}
          stroke="rgba(255,255,255,0.08)"
          strokeWidth={stroke}
          fill="none"
        />
        {/* Progress */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={r}
          stroke="url(#g)"
          strokeWidth={stroke}
          strokeLinecap="round"
          strokeDasharray={`${dash} ${c - dash}`}
          strokeDashoffset={c * 0.25}
          fill="none"
          transform={`rotate(-90 ${size / 2} ${size / 2})`}
        />
        <defs>
          <linearGradient id="g" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="#60a5fa" />
            <stop offset="100%" stopColor="#8b5cf6" />
          </linearGradient>
        </defs>
      </svg>

      <div className="absolute inset-0 grid place-items-center pointer-events-none">
        <div className="text-center leading-tight">
          <div className="text-[11px] text-white/70">{label}</div>
          <div className="text-sm font-semibold">{value}%</div>
        </div>
      </div>
    </div>
  );
}

