// src/pages/Home.jsx
import PageWrapper from "../components/PageWrapper";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Grid3X3,
  User2,
  IdCard,
  Rss,
  Linkedin,
  Github,
  Sparkles,
  Code2,
  Workflow,
  Gauge,
  Trophy,
  MessageSquareText,
  LogOut,
} from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

/* Animations (gentle + visible) */
const gridStagger = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.35, staggerChildren: 0.16 },
  },
};
const popIn = (i = 0) => {
  const d = i % 3;
  const offset = d === 0 ? { x: -60 } : d === 1 ? { x: 60 } : { y: 60 };
  return {
    hidden: { opacity: 0, scale: 0.96, ...offset },
    visible: {
      opacity: 1,
      scale: 1,
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 120, damping: 22, mass: 1.2 },
    },
  };
};

/* Floating link-out icon (door-with-arrow) */
const IconGo = ({ to, href, label = "Open", size = 16 }) => {
  const base =
    "absolute top-3 right-3 inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 p-2.5 hover:bg-white/20 transition z-20";
  const icon = <LogOut size={size} />;
  return href ? (
    <a
      className={base}
      href={href}
      target="_blank"
      rel="noreferrer"
      aria-label={label}
    >
      {icon}
    </a>
  ) : (
    <Link className={base} to={to} aria-label={label}>
      {icon}
    </Link>
  );
};

// a small helper: adds a safe gap on the right so IconGo never overlaps content/icons
const rightSafeGap = "pr-14"; // ~56px reserved area

export default function Home() {
  const { language } = useLanguage();
  const t = translations[language] ?? {};

  const TXT = {
    name: "Mohammad Rajabi",
    role: "Web Developer",
    tag: "APIs, React, Tailwind, Framer",
    moreAbout: "Credentials",
    projects: t.projects || "Projects",
    blog: "My Blog",
    services: "Services Offering",
    profiles: "Profiles",
    yearsExp: "YEARS\nEXPERIENCE",
    totalProjects: "TOTAL\nPROJECTS",
    lets: "Let’s",
    workTogether: "Work together",
  };

  return (
    <PageWrapper>
      {/* add bottom padding because your global footer is fixed */}
      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-24 md:pb-28">
        <div className="mx-auto max-w-6xl px-4 py-10 md:py-14">
          {/* GRID */}
          <motion.div
            className="grid grid-cols-12 gap-4 md:gap-5"
            variants={gridStagger}
            initial="hidden"
            animate="visible"
          >
            {/* ROW 1 */}
            {/* Profile (7 cols) */}
            <motion.div
              variants={popIn(0)}
              whileHover={{ scale: 1.01 }}
              className="col-span-15 md:col-span-7 rounded-2xl bg-gradient-to-b from-[#1b1c1f] to-[#131416] shadow-lg p-5 flex items-center gap-5"
            >
              <img
                src="/certs/mmdusa.jpg"
                alt="profile"
                className="h-28 w-30 rounded-xl object-cover"
              />
              <div className="flex-1">
                <p className="text-xs text-white/50">{TXT.role}</p>
                <h2 className="mt-1 text-2xl font-bold">{TXT.name}</h2>
                <p className="mt-1 text-sm text-white/60">{TXT.tag}</p>
              </div>
              {/* profile silhouette icon (non-action, so no collision) */}
              <User2 className="opacity-40 shrink-0" />
            </motion.div>

            {/* Right column (5 cols) stacked: Credentials + Projects */}
            <div className="col-span-12 md:col-span-5 grid grid-cols-1 gap-4 md:gap-5">
              {/* Credentials */}
              <motion.div
                variants={popIn(1)}
                whileHover={{ scale: 1.01 }}
                className={`relative rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between ${rightSafeGap}`}
              >
                <IconGo to="/credentials" label="Open credentials" />
                <div>
                  <p className="text-xs text-white/50">More about me</p>
                  <h3 className="mt-1 text-lg font-semibold">
                    {TXT.moreAbout}
                  </h3>
                </div>
                {/* main icon on the right, but inside the safe gap boundary */}
                <div className="shrink-0 mr-1 pointer-events-none">
                  <IdCard />
                </div>
              </motion.div>

              {/* Projects */}
              <motion.div
                variants={popIn(2)}
                whileHover={{ scale: 1.01 }}
                className={`relative rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between ${rightSafeGap}`}
              >
                <IconGo to="/projects" label="Open projects" />
                <div>
                  <p className="text-xs text-white/50">Showcase</p>
                  <h3 className="mt-1 text-lg font-semibold">{TXT.projects}</h3>
                </div>
                <div className="shrink-0 mr-1 pointer-events-none">
                  <Grid3X3 />
                </div>
              </motion.div>
            </div>

            {/* ROW 2: Blog (4) + Services (6) + Profiles (2) */}
            {/* Blog */}
            <motion.div
              variants={popIn(3)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-4 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between ${rightSafeGap}`}
            >
              <IconGo to="/blog" label="Open blog" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Blog</p>
                <h3 className="text-lg font-semibold">{TXT.blog}</h3>
              </div>
              <div className="shrink-0 mr-1 pointer-events-none">
                <Rss />
              </div>
            </motion.div>

            {/* Services */}
            <motion.div
              variants={popIn(4)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between ${rightSafeGap}`}
            >
              <IconGo to="/services" label="Open services" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Specialization</p>
                <h3 className="text-lg font-semibold">{TXT.services}</h3>
              </div>
              {/* tech glyphs like the reference */}
              <div className="flex items-center gap-4 opacity-80 shrink-0 mr-1 pointer-events-none">
                <Code2 />
                <Workflow />
                <Sparkles />
              </div>
            </motion.div>

            {/* Profiles (with round social pills) */}
            <motion.div
              variants={popIn(5)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-2 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between ${rightSafeGap}`}
            >
              <IconGo to="/profiles" label="Open profiles" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Stay with me</p>
                <h3 className="text-lg font-semibold">{TXT.profiles}</h3>
              </div>
              <div className="flex items-center gap-3 shrink-0 mr-1">
                <a
                  href="https://linkedin.com/in/mohammadhosseinrajabi"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-3 hover:bg-white/20 transition"
                  aria-label="LinkedIn"
                >
                  <Linkedin size={18} />
                </a>
                <a
                  href="https://github.com/yourusername"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-3 hover:bg-white/20 transition"
                  aria-label="GitHub"
                >
                  <Github size={18} />
                </a>
              </div>
            </motion.div>

            {/* ROW 3: Stats (3 + 3) + CTA (6) */}
            <motion.div
              variants={popIn(6)}
              whileHover={{ scale: 1.01 }}
              className="col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2"
            >
              <div className="rounded-2xl bg-black/20 p-4">
                <Gauge />
              </div>
              <div className="text-3xl font-bold">01</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                {TXT.yearsExp}
              </p>
            </motion.div>

            <motion.div
              variants={popIn(7)}
              whileHover={{ scale: 1.01 }}
              className="col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2"
            >
              <div className="rounded-2xl bg-black/20 p-4">
                <Trophy />
              </div>
              <div className="text-3xl font-bold">06</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                {TXT.totalProjects}
              </p>
            </motion.div>

            {/* CTA */}
            <motion.div
              variants={popIn(8)}
              whileHover={{ scale: 1.01 }}
              className={`relative col-span-12 md:col-span-6 rounded-2xl bg-gradient-to-br from-[#17181b] to-[#101114] p-6 shadow-lg overflow-hidden ${rightSafeGap}`}
            >
              <IconGo to="/contact" label="Contact" size={18} />
              {/* move the speech bubble a bit lower so it never collides with IconGo */}
              <div className="absolute right-4 top-14 opacity-20 pointer-events-none">
                <MessageSquareText size={40} />
              </div>
              <div className="flex flex-col sm:flex-row items-center sm:items-end gap-4 justify-between">
                <div>
                  <p className="text-sm text-white/60">{TXT.lets}</p>
                  <h3 className="text-2xl font-extrabold">
                    {TXT.workTogether}
                  </h3>
                </div>
              </div>
            </motion.div>
          </motion.div>
        </div>
      </section>
    </PageWrapper>
  );
}
