import PageWrapper from "../components/PageWrapper";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";
import { useMemo, useRef, useState, useEffect } from "react";

// Replace your SittingDeskIllustration with this “more realistic” version.
function SittingDeskIllustration({
  className = "",
  accentStart = "#60a5fa",
  accentEnd = "#a78bfa",
}) {
  return (
    <svg
      viewBox="0 0 540 420"
      xmlns="http://www.w3.org/2000/svg"
      role="img"
      aria-label="Person sitting on desk with computer"
      className={className}
    >
      <defs>
        <linearGradient id="gAccent" x1="0" y1="0" x2="1" y2="1">
          <stop offset="0%" stopColor={accentStart} />
          <stop offset="100%" stopColor={accentEnd} />
        </linearGradient>
        <filter id="softShadow" x="-20%" y="-20%" width="140%" height="140%">
          <feDropShadow dx="0" dy="6" stdDeviation="8" floodOpacity="0.25" />
        </filter>
      </defs>

      <rect width="540" height="420" fill="none" />

      {/* === Desk top === */}
      <rect
        x="86"
        y="252"
        width="368"
        height="14"
        rx="7"
        fill="#e8e8ea"
        filter="url(#softShadow)"
      />
      <rect x="446" y="246" width="22" height="20" rx="6" fill="#e8e8ea" />

      {/* === Pedestals === */}
      <g fill="#efeff1">
        <rect x="120" y="266" width="90" height="116" rx="10" />
        <rect x="392" y="266" width="90" height="116" rx="10" />
      </g>
      {[0, 1, 2, 3].map((i) => (
        <g key={`L${i}`} transform={`translate(0,${i * 26})`}>
          <rect x="134" y="284" width="64" height="16" rx="6" fill="#e1e1e6" />
          <circle cx="166" cy="292" r="3.2" fill="#b6b7bf" />
        </g>
      ))}
      {[0, 1, 2, 3].map((i) => (
        <g key={`R${i}`} transform={`translate(0,${i * 26})`}>
          <rect x="406" y="284" width="64" height="16" rx="6" fill="#e1e1e6" />
          <circle cx="438" cy="292" r="3.2" fill="#b6b7bf" />
        </g>
      ))}

      {/* === Keyboard + mouse === */}
      <rect x="260" y="262" width="88" height="6" rx="3" fill="#cfd0d6" />
      <rect x="352" y="262" width="14" height="6" rx="3" fill="#cfd0d6" />

      {/* === Monitor === */}
      <rect x="308" y="156" width="118" height="76" rx="12" fill="#2a2b30" />
      <rect x="316" y="166" width="102" height="56" rx="8" fill="#0f1013" />
      {[0, 1, 2, 3, 4, 5].map((i) => (
        <rect
          key={i}
          x="324"
          y={176 + i * 8}
          width={84 - (i % 2) * 18}
          height="4"
          rx="2"
          fill="url(#gAccent)"
        />
      ))}
      <rect x="356" y="234" width="12" height="12" rx="3" fill="#2a2b30" />
      <rect x="338" y="246" width="48" height="6" rx="3" fill="#2a2b30" />

      {/* === Person (more realistic) === */}
      <g transform="translate(0,0)">
        {/* shadows */}
        <ellipse
          cx="206"
          cy="380"
          rx="26"
          ry="9"
          fill="#141519"
          opacity="0.35"
        />
        <ellipse
          cx="258"
          cy="380"
          rx="22"
          ry="8"
          fill="#141519"
          opacity="0.35"
        />

        {/* right leg (dangling) */}
        <path
          d="M224 268 q-10 30 -20 62 l-4 12 h-28 l9-30 q6-20 8-44 z"
          fill="#1b1c20"
        />
        {/* shoe (right) */}
        <path
          d="M162 334 q34-2 36 12 q-2 14 -38 14 h-10 v-12 q6-12 12-14z"
          fill="#0e0f12"
        />

        {/* left leg (bent, heel on pedestal edge) */}
        <path
          d="M268 270 q10 26 10 48 v28 h-26 q-2-22 -10-42 q-4-10 6-18z"
          fill="#1b1c20"
        />
        {/* shoe (left) */}
        <path
          d="M232 346 q30 0 34 12 q-2 10 -30 10 h-20 v-12 q6-8 16-10z"
          fill="#0e0f12"
        />

        {/* hips / shorts overlap to soften joint */}
        <ellipse
          cx="236"
          cy="270"
          rx="28"
          ry="10"
          fill="#101114"
          opacity="0.35"
        />

        {/* torso (tee) */}
        <path
          d="M238 212 q-18 14 -20 34 v18 h74 v-16 q0-24 -18-40z"
          fill="url(#gAccent)"
          opacity="0.85"
        />
        {/* jacket (open) */}
        <path
          d="M236 210 q-22 12 -30 34 l-12 30 h24 l10-20 28-8 28 8 10 20 h24l-12-30 q-8-22 -26-34z"
          fill="#0f1013"
        />
        {/* inner torso (shadow) */}
        <path
          d="M238 212 q-14 12 -16 30 v8 h64 v-8 q0-20 -16-30z"
          fill="#2b2d34"
        />

        {/* neck + head */}
        <path
          d="M246 194 h-2 q-10 0 -10 12 v8 h28 v-8 q0-12 -10-12z"
          fill="#eecdbd"
        />
        <circle cx="247" cy="182" r="16" fill="#f2d3c1" />

        {/* hair closer to reference */}
        <path
          d="M233 182 q8-18 28-16 q12 2 16 12 q-12-6 -26 2 q-6 4 -18 2z"
          fill="#1a1b1f"
        />
        <path
          d="M260 170 q10 6 10 16 q-10-6 -18-2 q0-10 8-14z"
          fill="#1a1b1f"
        />

        {/* face hints (minimal) */}
        <path
          d="M242 188 q4 2 10 0"
          stroke="#e6b9a7"
          strokeWidth="2"
          strokeLinecap="round"
        />
        <circle cx="240" cy="184" r="1.6" fill="#1a1b1f" />
        <circle cx="252" cy="184" r="1.6" fill="#1a1b1f" />

        {/* arms: right relaxed, left resting on thigh */}
        <path d="M214 238 q-12 10 -14 20 l20 6 12-12z" fill="#2b2d34" />
        <path d="M272 236 q16 10 18 20 l-22 6 -10-12z" fill="#2b2d34" />
      </g>
    </svg>
  );
}

/* === Circular gauge === */
function CircleGauge({
  label,
  percent,
  size = "clamp(96px, 11vw, 120px)",
  className = "",
}) {
  const p = Math.min(100, Math.max(0, percent));
  const angle = p * 3.6;
  const styleSize = typeof size === "number" ? `${size}px` : size;
  return (
    <div className={`flex flex-col items-center ${className}`}>
      <div
        className="relative rounded-full shadow-[inset_0_0_0_2px_rgba(0,0,0,0.45)]"
        style={{ width: styleSize, height: styleSize }}
        aria-label={`${label} ${p}%`}
      >
        <div
          className="absolute inset-0 rounded-full"
          style={{
            background: `conic-gradient(#60a5fa 0deg, #a78bfa ${angle}deg, #1b1c20 ${angle}deg 360deg)`,
          }}
        />
        <div className="absolute inset-2 rounded-full bg-[#0f1013] grid place-items-center">
          <div className="text-center leading-tight">
            <div className="text-[11px] text-white/65">{label}</div>
            <div className="mt-[2px] text-[14px] font-semibold text-white/85">
              {p}%
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

/* === Helpers for slider === */
const chunk = (arr, n) =>
  Array.from({ length: Math.ceil(arr.length / n) }, (_, i) =>
    arr.slice(i * n, i * n + n)
  );

const useSlider = () => {
  const ref = useRef(null);
  const [page, setPage] = useState(0);
  useEffect(() => {
    const el = ref.current;
    if (!el) return;
    const onScroll = () => setPage(Math.round(el.scrollLeft / el.clientWidth));
    el.addEventListener("scroll", onScroll, { passive: true });
    return () => el.removeEventListener("scroll", onScroll);
  }, []);
  const goTo = (i) =>
    ref.current?.scrollTo({
      left: i * ref.current.clientWidth,
      behavior: "smooth",
    });
  return { ref, page, goTo };
};

export default function About() {
  const { language } = useLanguage();
  const t = translations[language];

  const gauges = [
    { label: "Next.js", percent: 70 },
    { label: "React.js", percent: 70 },
    { label: "Tailwind", percent: 70 },
    { label: "JavaScript", percent: 60 },
    { label: "CSS", percent: 70 },
    { label: "HTML", percent: 80 },
  ];

  const slides = useMemo(() => chunk(gauges, 4), [gauges]); // 4 per page
  const { ref: railRef, page, goTo } = useSlider();

  return (
    <PageWrapper>
      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white relative pb-20 md:pb-24">
        <div className="mx-auto max-w-6xl px-4 pt-6 md:pt-8">
          {/* ===== CARD ===== */}
          <div className="relative rounded-3xl bg-gradient-to-b from-[#1b1c1f] to-[#131416] shadow-lg p-6 md:p-8">
            {/* sparkle */}
            <svg
              width="34"
              height="34"
              viewBox="0 0 24 24"
              className="absolute left-4 top-3 opacity-60"
            >
              <path
                d="M12 3v4m0 10v4M3 12h4m10 0h4M6 6l2.5 2.5M15.5 15.5 18 18M6 18l2.5-2.5M15.5 8.5 18 6"
                stroke="white"
                strokeOpacity=".6"
                strokeWidth="1.2"
                fill="none"
                strokeLinecap="round"
              />
            </svg>

            {/* text + illustration */}
            <div className="grid grid-cols-12 gap-6 md:gap-10 items-start">
              <div className="col-span-12 md:col-span-6">
                <h1 className="text-[28px] sm:text-[32px] md:text-[34px] font-extrabold mb-4">
                  Mohammad Rajabi
                </h1>
                <p className="text-[14px] sm:text-[15px] leading-7 text-white/90 text-justify max-w-[680px]">
                  {t.intro1}
                  <strong> React</strong>, <strong> Tailwind CSS</strong>,{" "}
                  <strong> Framer Motion</strong>. {t.intro2} {t.intro3}
                </p>
              </div>

              <div className="col-span-12 md:col-span-6 flex justify-center md:justify-end">
                <SittingDeskIllustration className="w-[92%] max-w-[520px] -mt-2 md:mt-0" />
              </div>
            </div>

            {/* ===== METERS ===== */}
            <div className="mt-8 md:mt-10">
              {/* LG+ : keep 6 in one row, shrink smoothly */}
              <div className="hidden lg:flex lg:flex-nowrap items-center justify-between gap-4 xl:gap-6">
                {gauges.map((g) => (
                  <CircleGauge
                    key={g.label}
                    label={g.label}
                    percent={g.percent}
                    size="clamp(88px, 9.5vw, 120px)"
                    className="shrink-0"
                  />
                ))}
              </div>

              {/* <LG : paged slider, 4 per page, no peeking */}
              <div
                ref={railRef}
                className="lg:hidden overflow-x-auto snap-x snap-mandatory [scroll-snap-stop:always]
                           [scrollbar-width:none] [-ms-overflow-style:none]"
                style={{ WebkitOverflowScrolling: "touch" }}
              >
                <div className="flex flex-nowrap">
                  {slides.map((group, idx) => (
                    <div key={idx} className="min-w-full shrink-0 snap-start">
                      <div className="grid grid-cols-4 py-2">
                        {group.map((g) => (
                          <div
                            key={g.label}
                            className="flex items-center justify-center"
                          >
                            <CircleGauge
                              label={g.label}
                              percent={g.percent}
                              size="clamp(76px, 20vw, 104px)"
                              className="shrink-0"
                            />
                          </div>
                        ))}
                        {Array.from({ length: 4 - group.length }).map(
                          (_, i) => (
                            <div
                              key={`ph-${i}`}
                              className="flex items-center justify-center"
                            />
                          )
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* dots */}
              <div className="lg:hidden mt-2 flex items-center justify-center gap-2">
                {slides.map((_, i) => (
                  <button
                    key={i}
                    onClick={() => goTo(i)}
                    aria-label={`Go to page ${i + 1}`}
                    className={`h-2 w-2 rounded-full transition-all ${
                      i === page ? "w-4 bg-white/90" : "bg-white/40"
                    }`}
                  />
                ))}
              </div>
            </div>
          </div>

          {/* ===== BOTTOM CARDS ===== */}
          <div className="mt-6 md:mt-8 grid grid-cols-12 gap-4 md:gap-5">
            <div className="col-span-12 md:col-span-6 rounded-3xl bg-[#17181b] shadow-lg p-6">
              <h3 className="text-sm font-semibold tracking-wide text-white/70 mb-6">
                REGISTERED COURSES
              </h3>
              <div className="space-y-8 text-[13.5px]">
                <div>
                  <div className="text-white/70">05/2019 – 11/2019</div>
                  <div className="mt-1 font-semibold">
                    Html, Css, JavaScript
                  </div>
                  <div className="text-white/60">Everest College</div>
                </div>
                <div>
                  <div className="text-white/70">2020 – 2021</div>
                  <div className="mt-1 font-semibold">
                    Html, Css, Bootstrap, JavaScript
                  </div>
                  <div className="text-white/60">ValaCoding</div>
                </div>
                <div>
                  <div className="text-white/70">05/2024 – 12/2024</div>
                  <div className="mt-1 font-semibold">
                    Html, Css, JavaScript, Git, React, Next.js
                  </div>
                  <div className="text-white/60">BotoStart</div>
                </div>
              </div>
            </div>

            <div className="col-span-12 md:col-span-6 rounded-3xl bg-[#17181b] shadow-lg p-6">
              <h3 className="text-sm font-semibold tracking-wide text-white/70 mb-6">
                {t.Education}
              </h3>
              <div className="space-y-8 text-[13.5px]">
                <div>
                  <div className="text-white/70">2013 – 2018</div>
                  <div className="mt-1 font-semibold">
                    {t.bachelorTitle ||
                      "Bachelor Degree in Material Engineering"}
                  </div>
                  <div className="text-white/60">
                    {t.bachelorPlace ||
