// src/pages/Home.jsx
import PageWrapper from "../components/PageWrapper";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Grid3X3,
  User2,
  FileBadge2,
  PenSquare,
  Linkedin,
  Github,
  Sparkles,
  Code2,
  Workflow,
  Gauge,
  Trophy,
  MessageSquareText,
  LogOut,
} from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";
import { useEffect, useState } from "react";

/* ---- Animations ---- */

// Parent stagger (slower)
const gridStagger = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.3, staggerChildren: 0.14 },
  },
};

// Alternating pop-in with gentler spring
const popIn = (i = 0) => {
  const d = i % 3; // 0:left, 1:right, 2:up
  const offset = d === 0 ? { x: -50 } : d === 1 ? { x: 50 } : { y: 50 };
  return {
    hidden: { opacity: 0, scale: 0.97, ...offset },
    visible: {
      opacity: 1,
      scale: 1,
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 140, damping: 20, mass: 1.1 },
    },
  };
};

// floating icon button pinned to top-right of a card
const IconGo = ({ to, href, label = "Open", size = 16 }) => {
  const base =
    "absolute top-3 right-3 inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 p-2 hover:bg-white/20 transition";
  const icon = <LogOut size={size} />;
  if (href) {
    return (
      <a
        className={base}
        href={href}
        target="_blank"
        rel="noreferrer"
        aria-label={label}
      >
        {icon}
      </a>
    );
  }
  return (
    <Link className={base} to={to} aria-label={label}>
      {icon}
    </Link>
  );
};

export default function Home() {
  const { language } = useLanguage();
  const t = translations[language] ?? {};

  // ensure animation runs AFTER mount (fixes "no show" on hard refresh)
  const [mounted, setMounted] = useState(false);
  useEffect(() => setMounted(true), []);

  // Safe fallbacks
  const TXT = {
    name: "Mohammad Rajabi",
    role: "Web Developer",
    tag: "APIs, React, Tailwind, Framer",
    moreAbout: "Credentials",
    projects: t.projects || "Projects",
    blog: "My Blog",
    services: "Services Offering",
    profiles: "Profiles",
    yearsExp: "YEARS\nEXPERIENCE",
    totalProjects: "TOTAL\nPROJECTS",
    lets: "Let’s",
    workTogether: "Work together",
    contact: t.contact || "Contact",
  };

  return (
    <PageWrapper>
      <section className="min-h-[92vh] w-full bg-[#0f0f11] text-white">
        <div className="mx-auto max-w-6xl px-4 py-10 md:py-14">
          {/* GRID */}
          <motion.div
            className="grid grid-cols-12 gap-4 md:gap-5"
            variants={gridStagger}
            initial="hidden"
            animate={mounted ? "visible" : "hidden"} // <- starts after mount
          >
            {/* 0 — Profile Card */}
            <motion.div
              variants={popIn(0)}
              whileHover={{ scale: 1.01 }}
              className="col-span-12 md:col-span-7 rounded-2xl bg-gradient-to-b from-[#1b1c1f] to-[#131416] shadow-lg p-5 flex items-center gap-5"
            >
              <img
                src="https://avatars.githubusercontent.com/u/9919?v=4"
                alt="profile"
                className="h-28 w-28 rounded-xl object-cover"
              />
              <div className="flex-1">
                <p className="text-xs text-white/50">{TXT.role}</p>
                <h2 className="mt-1 text-2xl font-bold">{TXT.name}</h2>
                <p className="mt-1 text-sm text-white/60">{TXT.tag}</p>
              </div>
              <User2 className="opacity-40" />
            </motion.div>

            {/* 1 — Credentials */}
            <motion.div
              variants={popIn(1)}
              whileHover={{ scale: 1.01 }}
              className="relative col-span-12 md:col-span-5 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between"
            >
              <IconGo to="/credentials" label="Open credentials" />
              <div>
                <p className="text-xs text-white/50">More about me</p>
                <h3 className="mt-1 text-lg font-semibold">{TXT.moreAbout}</h3>
              </div>
              <FileBadge2 />
            </motion.div>

            {/* 2 — Projects */}
            <motion.div
              variants={popIn(2)}
              whileHover={{ scale: 1.01 }}
              className="relative col-span-12 md:col-span-5 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between"
            >
              <IconGo to="/projects" label="Open projects" />
              <div>
                <p className="text-xs text-white/50">Showcase</p>
                <h3 className="mt-1 text-lg font-semibold">{TXT.projects}</h3>
              </div>
              <Grid3X3 />
            </motion.div>

            {/* 3 — Blog */}
            <motion.div
              variants={popIn(3)}
              whileHover={{ scale: 1.01 }}
              className="relative col-span-12 md:col-span-7 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between"
            >
              <IconGo to="/blog" label="Open blog" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Blog</p>
                <h3 className="text-lg font-semibold">{TXT.blog}</h3>
              </div>
              <PenSquare />
            </motion.div>

            {/* 4 — Services */}
            <motion.div
              variants={popIn(4)}
              whileHover={{ scale: 1.01 }}
              className="relative col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between"
            >
              <IconGo to="/services" label="Open services" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Specialization</p>
                <h3 className="text-lg font-semibold">{TXT.services}</h3>
              </div>
              <div className="flex items-center gap-3 opacity-80">
                <Code2 />
                <Workflow />
                <Sparkles />
              </div>
            </motion.div>

            {/* 5 — Profiles */}
            <motion.div
              variants={popIn(5)}
              whileHover={{ scale: 1.01 }}
              className="relative col-span-12 md:col-span-2 rounded-2xl bg-[#17181b] p-5 shadow-lg flex items-center justify-between"
            >
              <IconGo to="/profiles" label="Open profiles" />
              <div className="space-y-1">
                <p className="text-xs text-white/50">Stay with me</p>
                <h3 className="text-lg font-semibold">{TXT.profiles}</h3>
              </div>
              <div className="flex items-center gap-2">
                <a
                  href="https://linkedin.com/in/mohammadhosseinrajabi"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-2 hover:bg-white/20 transition"
                  aria-label="LinkedIn"
                >
                  <Linkedin size={18} />
                </a>
                <a
                  href="https://github.com/yourusername"
                  target="_blank"
                  rel="noreferrer"
                  className="rounded-full bg-white/10 p-2 hover:bg-white/20 transition"
                  aria-label="GitHub"
                >
                  <Github size={18} />
                </a>
              </div>
            </motion.div>

            {/* 6 — Stat: Years */}
            <motion.div
              variants={popIn(6)}
              whileHover={{ scale: 1.01 }}
              className="col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2"
            >
              <div className="rounded-2xl bg-black/20 p-4">
                <Gauge />
              </div>
              <div className="text-3xl font-bold">01</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                {TXT.yearsExp}
              </p>
            </motion.div>

            {/* 7 — Stat: Projects */}
            <motion.div
              variants={popIn(7)}
              whileHover={{ scale: 1.01 }}
              className="col-span-12 md:col-span-3 rounded-2xl bg-[#17181b] p-6 shadow-lg flex flex-col items-center justify-center gap-2"
            >
              <div className="rounded-2xl bg-black/20 p-4">
                <Trophy />
              </div>
              <div className="text-3xl font-bold">06</div>
              <p className="whitespace-pre-line text-center text-[11px] tracking-wide text-white/60">
                {TXT.totalProjects}
              </p>
            </motion.div>

            {/* 8 — CTA */}
            <motion.div
              variants={popIn(8)}
              whileHover={{ scale: 1.01 }}
              className="relative col-span-12 md:col-span-6 rounded-2xl bg-gradient-to-br from-[#17181b] to-[#101114] p-6 shadow-lg overflow-hidden"
            >
              <IconGo to="/contact" label="Contact" size={18} />
              <div className="absolute right-4 top-4 opacity-20">
                <MessageSquareText size={40} />
              </div>
              <div className="flex flex-col sm:flex-row items-center sm:items-end gap-4 justify-between">
                <div>
                  <p className="text-sm text-white/60">{TXT.lets}</p>
                  <h3 className="text-2xl font-extrabold">
                    {TXT.workTogether}
                  </h3>
                </div>
              </div>
            </motion.div>
          </motion.div>

          {/* Footer signature row */}
          <div className="mt-10 flex items-center justify-center gap-6 text-xs text-white/50">
            <span className="tracking-wide">
              © {new Date().getFullYear()} {TXT.name}
            </span>
            <span className="hidden sm:inline">—</span>
            <span className="hidden sm:inline">All rights reserved.</span>
          </div>
        </div>
      </section>
    </PageWrapper>
  );
}
