import PageWrapper from "../components/PageWrapper";
import { motion } from "framer-motion";
import HeroParticles from "../components/HeroParticles";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

export default function Home() {
  const { language } = useLanguage();
  const t = translations[language];

  // --- DATA (edit these as you like) ---
  const recommendations = [
    {
      name: "Gianandrea Siccardi",
      avatar: "https://i.pravatar.cc/150?img=12", // replace with real photo URL if you have one
      text: "I am writing to wholeheartedly endorse Mohammad for the position of AI Developer. He consistently shows initiative, attention to detail, and strong communication skills...",
    },
    {
      name: "Stefano Quer",
      avatar: "https://i.pravatar.cc/150?img=32",
      text: "Mohammad was my student from September 2023 to January 2024. He followed an intense course in computer engineering and always demonstrated curiosity and dedication...",
    },
  ];

  const achievements = [
    { title: "Machine Learning with Python (IBM)", img: "/certs/ibm-ml.png" },
    {
      title: "Python Project for Data Science (IBM)",
      img: "/certs/ibm-python-project.png",
    },
    {
      title: "Python for Data Science and AI (IBM)",
      img: "/certs/ibm-ds-ai.png",
    },
    {
      title: "Data Science Methodology (IBM)",
      img: "/certs/ibm-ds-methods.png",
    },
  ];
  // -------------------------------------

  return (
    <PageWrapper>
      {/* ===== HERO ===== */}
      <section className="w-full h-screen bg-blue-100 dark:bg-gray-900 text-gray-800 dark:text-white relative overflow-hidden">
        <HeroParticles />
        <div className="absolute inset-0 z-10 flex flex-col md:flex-row items-center justify-center px-6 md:px-20">
          {/* Left */}
          <motion.div
            className="md:w-1/2 text-center md:text-left mb-10 md:mb-0"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-4xl md:text-5xl font-bold text-blue-800 dark:text-blue-300 mb-4">
              {t.heroTitle}
            </h1>
            <p className="text-lg text-gray-700 dark:text-gray-300 mb-6">
              {t.heroSubtitle}
            </p>
            <a
              href="/contact"
              className="inline-block px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
            >
              {t.heroCTA}
            </a>
          </motion.div>

          {/* Right */}
          <motion.div
            className="md:w-1/2 flex justify-center"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <img
              src="https://cdn.dribbble.com/users/1162077/screenshots/3848914/programmer.gif"
              alt="Developer Illustration"
              className="rounded-xl shadow-md max-w-[90%] max-h-[70vh] object-contain"
            />
          </motion.div>
        </div>
      </section>

      {/* ===== RECOMMENDATIONS ===== */}
      <section className="w-full bg-gray-100 dark:bg-gray-950 text-gray-900 dark:text-white py-16">
        <div className="max-w-6xl mx-auto px-6">
          <h2 className="text-3xl md:text-4xl font-extrabold mb-8">
            {t.recommendationsTitle || "Cover Letters"}
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {recommendations.map((rec, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.4, delay: i * 0.1 }}
                className="bg-gray-900/90 dark:bg-black/60 text-white rounded-2xl p-6 shadow-xl flex items-start gap-4"
              >
                <img
                  src={rec.avatar}
                  alt={rec.name}
                  className="w-14 h-14 rounded-full object-cover ring-2 ring-white/20"
                />
                <div>
                  <h3 className="text-xl font-semibold mb-2">{rec.name}</h3>
                  <p className="text-white/80 leading-relaxed">{rec.text}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* ===== ACHIEVEMENTS / CERTIFICATES ===== */}
      <section className="w-full bg-gray-100 dark:bg-gray-950 text-gray-900 dark:text-white pb-20">
        <div className="max-w-6xl mx-auto px-6">
          <h2 className="text-3xl md:text-4xl font-extrabold mb-8">
            {t.achievementsTitle || "Achievements"}
          </h2>

          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-8 place-items-center">
            {achievements.map((a, i) => (
              <motion.figure
                key={i}
                initial={{ opacity: 0, scale: 0.95 }}
                whileInView={{ opacity: 1, scale: 1 }}
                viewport={{ once: true }}
                transition={{ duration: 0.35, delay: i * 0.05 }}
                className="flex flex-col items-center"
                title={a.title}
              >
                <img
                  src={a.img}
                  alt={a.title}
                  className="w-40 h-40 object-contain drop-shadow-xl rounded-full bg-white/5"
                />
                <figcaption className="mt-3 text-center text-sm opacity-80 max-w-[12rem]">
                  {a.title}
                </figcaption>
              </motion.figure>
            ))}
          </div>
        </div>
      </section>
    </PageWrapper>
  );
}
