// src/pages/Home.jsx
import PageWrapper from "../components/PageWrapper";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import {
  Grid3X3,
  User2,
  IdCard,
  Rss,
  Linkedin,
  Github,
  Sparkles,
  Code2,
  Workflow,
  Gauge,
  Trophy,
  MessageSquareText,
  LogOut,
} from "lucide-react";
import { useLanguage } from "../context/LanguageContext";
import { translations } from "../utils/translations";

/* Animations (gentle + visible) */
const gridStagger = {
  hidden: { opacity: 1 },
  visible: {
    opacity: 1,
    transition: { delayChildren: 0.35, staggerChildren: 0.16 },
  },
};
const popIn = (i = 0) => {
  const d = i % 3;
  const offset = d === 0 ? { x: -60 } : d === 1 ? { x: 60 } : { y: 60 };
  return {
    hidden: { opacity: 0, scale: 0.96, ...offset },
    visible: {
      opacity: 1,
      scale: 1,
      x: 0,
      y: 0,
      transition: { type: "spring", stiffness: 120, damping: 22, mass: 1.2 },
    },
  };
};

/* Floating link-out icon (door-with-arrow) */
const IconGo = ({ to, href, label = "Open", size = 16 }) => {
  const base =
    "absolute top-3 right-3 inline-flex items-center justify-center rounded-xl border border-white/10 bg-white/10 p-2.5 hover:bg-white/20 transition z-20";
  const icon = <LogOut size={size} />;
  return href ? (
    <a
      className={base}
      href={href}
      target="_blank"
      rel="noreferrer"
      aria-label={label}
    >
      {icon}
    </a>
  ) : (
    <Link className={base} to={to} aria-label={label}>
      {icon}
    </Link>
  );
};

